import { Component } from '@angular/core';
import { Store } from '@ngxs/store';
import { Observable } from 'rxjs';
import { ContentType, PureContentListComponent } from 'src/app/features/content-list/components/pure-content-list/pure-content-list.component';

@Component({
	selector: 'content-list',
	standalone: true,
	templateUrl: './content-list.component.html',
	styleUrl: './content-list.component.css',
	imports: [PureContentListComponent],
})
export class ContentListComponent {
	public content: Array<ContentType> = [];

	private content$: Observable<Array<ContentType>>;

	constructor(store: Store) {
		this.content$ = store.select((state) => state.content);
	}

	ngOnInit() {
		this.content$.subscribe((content) => {
			this.content = content;
		});
	}
}

import { Component, EventEmitter, HostBinding, HostListener, Input, Output } from '@angular/core';
import { DirectoryContentDirectory } from 'generated';
import { ISelectable } from 'src/app/shared/models/ISelectable';

@Component({
	selector: 'directory-list-item',
	standalone: true,
	templateUrl: './directory-list-item.component.html',
	styleUrl: './directory-list-item.component.css',
})
export class DirectoryListItemComponent implements ISelectable {
	@Input({ required: true })
	public metadata!: DirectoryContentDirectory;

	@Output()
	public onClickEmitter: EventEmitter<ISelectable> = new EventEmitter();

	@HostBinding('class.selected')
	public selected: boolean = false;

	@HostListener('click')
	public onClick() {
		this.onClickEmitter.emit(this);
	}

	public select(): void {
		this.selected = true;
	}

	public unselect(): void {
		this.selected = false;
	}

	protected formatBytes(bytes: number, decimals: number = 2): string {
		if (!+bytes) return '0 B';

		const k = 1024;
		const dm = decimals < 0 ? 0 : decimals;
		const sizes = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];

		const i = Math.floor(Math.log(bytes) / Math.log(k));

		return `${parseFloat((bytes / Math.pow(k, i)).toFixed(dm))} ${sizes[i]}`;
	}

	protected formatDate(dateOrString: Date | string): string {
		const date = dateOrString instanceof Date ? dateOrString : new Date(Date.parse(dateOrString));

		const month = date.toLocaleDateString('de-DE', { month: 'short' });
		const year = date.toLocaleDateString('de-DE', { year: '2-digit' });

		return `${date.getDay()} ${month} ${year}`;
	}
}

