import { Component, ElementRef, EventEmitter, Output, ViewChild } from '@angular/core';
import { ISelectable } from 'src/app/shared/models/ISelectable';
import { SelectEvent } from 'src/app/shared/models/SelectedEvent';

@Component({
	selector: 'checkbox',
	standalone: true,
	templateUrl: './checkbox.component.html',
	styleUrl: './checkbox.component.css',
})
export class CheckboxComponent implements ISelectable {
	@ViewChild('input')
	public input!: ElementRef<HTMLInputElement>;

	@Output()
	public onClick: EventEmitter<SelectEvent> = new EventEmitter();

	public onClickHandler(event: PointerEvent | MouseEvent) {
		const checked = (event.target as HTMLInputElement).checked;

		this.onClick.emit({ selected: checked, ctrl: event.ctrlKey, shift: event.shiftKey });
	}

	public select(): void {
		this.input.nativeElement.checked = true;
	}

	public unselect(): void {
		this.input.nativeElement.checked = false;
	}
}

import { Component } from '@angular/core';
import { Store } from '@ngxs/store';
import { Observable } from 'rxjs';
import { ContentType, PureContentListComponent } from 'src/app/features/content-list/components/pure-content-list/pure-content-list.component';

@Component({
	selector: 'content-list',
	standalone: true,
	templateUrl: './content-list.component.html',
	styleUrl: './content-list.component.css',
	imports: [PureContentListComponent],
})
export class ContentListComponent {
	public content: Array<ContentType> = [];

	private content$: Observable<Array<ContentType>>;

	constructor(store: Store) {
		this.content$ = store.select((state) => state.content);
	}

	ngOnInit() {
		this.content$.subscribe((content) => {
			this.content = content;
		});
	}
}

import { Component, HostBinding, Input } from '@angular/core';
import { DirectoryContentDirectory } from 'generated';
