import { Component, ElementRef, HostBinding, HostListener, Input } from '@angular/core';

export enum Type {
	Minimalistic = 'minimalistic',
	Rounded = 'rounded',
}

export enum Thickness {
	Thick = 'thick',
	Regular = 'regular',
	Thin = 'thin',
}

const ThicknessMap: Record<Thickness, number> = {
	[Thickness.Thick]: 0.16,
	[Thickness.Regular]: 0.08,
	[Thickness.Thin]: 0.04,
};

@Component({
	selector: 'loading-spinner',
	standalone: true,
	templateUrl: './loading-spinner.component.html',
	styleUrl: './loading-spinner.component.css',
})
export class LoadingSpinnerComponent {
	/**
	 * The thickness of the spinner.
	 * @type {Thickness}
	 */
	@Input()
	public thickness: Thickness = Thickness.Regular;

	/**
	 * The type of the spinner.
	 * @type {Type}
	 */
	@Input()
	public type: Type = Type.Rounded;

	/**
	 * Reference to the host element.
	 * @type {ElementRef<HTMLDivElement>}
	 */
	private readonly hostRef: ElementRef<HTMLDivElement>;

	/**
	 * Host binding for setting the thickness variable for CSS use.
	 * @type {string}
	 */
	@HostBinding('style.--thickness')
	private calculatedThickness!: string;

	/**
	 * Creates a new LoadingSpinnerComponent instance.
	 * @constructor
	 *
	 * @param   {ElementRef}              hostRef the entityManager
	 * @returns {LoadingSpinnerComponent}         the LoadingSpinnerComponent instance
	 */
	public constructor(hostRef: ElementRef) {
		this.hostRef = hostRef;
	}

	public ngAfterViewInit() {
		this.applyThickness();
	}

	public ngOnChanges() {
		this.applyThickness();
	}

	@HostBinding('class')
	get classList(): string {
		return `${this.thickness.toString()} ${this.type.toString()}`;
	}

	@HostListener('window:resize')
	private onResize() {
		this.applyThickness();
	}

	/**
	 * Calculate the thickness of the spinner based off its computed width.
	 *
	 * @param   {number} width the width of the component
	 * @returns {number}       the calculated thickness
	 */
	private calculateThickness(width: number) {
		return width * ThicknessMap[this.thickness];
	}

	/**
	 * Calculate the thickness, format for CSS and assign to host binding.
	 */
	private applyThickness() {
		this.calculatedThickness = `${this.calculateThickness(this.hostRef.nativeElement.clientWidth)}px`;
	}
}

import { Component, ElementRef, HostBinding, HostListener, Input, QueryList, ViewChildren } from '@angular/core';
import { DirectoryContentDirectory, DirectoryContentFile } from 'generated';
import { DirectoryListItemComponent } from 'src/app/features/content-list/components/pure-content-list/components/directory-list-item/directory-list-item.component';
import { FileListItemComponent } from 'src/app/features/content-list/components/pure-content-list/components/file-list-item/file-list-item.component';
import { SelectableListItemComponent } from 'src/app/features/content-list/components/pure-content-list/components/selectable-list-item/selectable-list-item.component';
import { SelectEvent } from 'src/app/shared/models/SelectedEvent';

export enum Type {
	Directory,
	File,
}

export type Directory = DirectoryContentDirectory & {
	type: Type.Directory;
};

export type File = DirectoryContentFile & {
	type: Type.File;
};

export type ContentType = File | Directory;

@Component({
	selector: 'pure-content-list',
	standalone: true,
	templateUrl: './pure-content-list.component.html',
	styleUrl: './pure-content-list.component.css',
	imports: [FileListItemComponent, DirectoryListItemComponent, SelectableListItemComponent],
})
export class PureContentListComponent {
	private ref: ElementRef;

	private selected: Set<number> = new Set();

	@HostBinding('class.in-select-mode')
	public isInSelectMode: boolean = false;

	private lastSelectedId!: number;

	@ViewChildren(SelectableListItemComponent)
	private children!: QueryList<SelectableListItemComponent>;

	@Input()
	public content: Array<ContentType> = [];

	constructor(ref: ElementRef) {
		this.ref = ref;
	}

	@HostListener('document:click', ['$event'])
	public clickOut(event: Event) {
		if (!this.ref.nativeElement.contains(event.target)) {
			this.selected.clear();
			this.isInSelectMode = false;
		}
	}

	public selectAll() {
		this.isInSelectMode = true;
		this.selected.clear();

		this.children.map((c) => {
			c.select();
			this.selected.add(c.id);
		});
	}

	public unselectAll() {
		this.selected.clear();

		this.children.map((c) => {
			c.unselect();
		});

		this.isInSelectMode = false;
	}

	public isFile(content: ContentType): content is File {
		return content.type === Type.File;
	}

	public onClick(event: SelectEvent) {
		const component: SelectableListItemComponent | undefined = this.children.get(event.id!);

		if (!component) {
			return;
		}

		if (this.lastSelectedId === undefined || (!event.ctrl && !event.shift)) {
			this.handleSingleSelect(event.selected, component);
		} else {
			this.handleRangeSelect(event.selected, component);
		}

		this.lastSelectedId = component.id;
	}

	private handleSingleSelect(selected: boolean, component: SelectableListItemComponent) {
		if (selected) {
			component.select();
			this.selected.add(component.id);
		} else {
			component.unselect();
			this.selected.delete(component.id);
		}

		this.isInSelectMode = this.selected.size > 0;
	}

	private handleRangeSelect(selected: boolean, component: SelectableListItemComponent) {
		const start = Math.min(this.lastSelectedId, component.id);
		const end = Math.max(this.lastSelectedId, component.id);

		if ((selected && end - start <= 2) || this.isUnselectedComponentInRange(start, end)) {
			this.selectRange(start, end);
		} else if (selected) {
			this.unselectRange(start, end);
		} else {
			this.unselectRange(start === component.id ? start + 1 : start, end);
			component.select();
			this.selected.add(component.id);
		}
	}

	private selectRange(start: number, end: number) {
		for (let i = start; i <= end; i++) {
			const child = this.children.get(i);

			if (!child) {
				continue;
			}

			child.select();
			this.selected.add(child.id);
		}
	}

	private unselectRange(start: number, end: number) {
		for (let i = start; i <= end; i++) {
			const child = this.children.get(i);

			if (!child) {
				continue;
			}

			child.unselect();
			this.selected.delete(child.id);
		}
	}

	private isUnselectedComponentInRange(start: number, end: number): boolean {
		return this.children.find((c) => c.id >= start && c.id <= end && !c.selected) !== undefined;
	}
}

import { Component, ContentChild, EventEmitter, HostBinding, Input, Output, ViewChild } from '@angular/core';
import { SelectableComponent } from 'src/app/features/content-list/components/pure-content-list/components/selectable-list-item/selectable.component';
import { CheckboxComponent } from 'src/app/shared/components/checkbox/checkbox.component';
import { ISelectable } from 'src/app/shared/models/ISelectable';
import { SelectEvent } from 'src/app/shared/models/SelectedEvent';

@Component({
	selector: 'selectable-list-item',
	standalone: true,
	templateUrl: './selectable-list-item.component.html',
	styleUrl: './selectable-list-item.component.css',
	imports: [CheckboxComponent],
})
export class SelectableListItemComponent implements ISelectable {
	@Input({ required: true })
	public id!: number;

	@HostBinding('class.selected')
	public selected: boolean = false;

	@ContentChild(SelectableComponent)
	public content!: ISelectable;

	@HostBinding('class.selectable')
	@Input('selectable')
	public isSelectable: boolean = true;

	@ViewChild(CheckboxComponent)
	public checkbox!: CheckboxComponent;

	@Output()
	public onClick: EventEmitter<SelectEvent> = new EventEmitter();

	public onClickHandler(event: SelectEvent) {
		this.onClick.emit({ id: this.id, ...event });
	}

	public select(): void {
		this.selected = true;

		this.content.select();
		this.checkbox.select();
	}

	public unselect(): void {
		this.selected = false;

		this.content.unselect();
		this.checkbox.unselect();
	}
}
